v2((data, context) => {
  // skip if already set
  if(context.cache.notFirstRun) {
    // set useList if it contains an 'l', but is not 'clq' which is show all
    const value = data.value[0];
    const useList = value != 'clq' && value.indexOf('l') != -1;
    // console.log('VarViewModel - debug value', data.value, useList);
    return {
      value: data.value,
      fields: [
        { name: 'UseForList', value: useList },
      ]
    };
  }

  // First-Run Scenario
  context.cache.notFirstRun = true;

  // new without name - set empty so it's still required
  if(!data.Name)
    return '';

  const hasQuery = data.Pipeline.length == 1;
  const hasList = data.UseForList;
  const hasContent = data.ContentTypeStaticName != '';
  const key = (hasContent || hasList ? 'c' : '') + (hasList ? 'l' : '') + (hasQuery ? 'q':'');
  if(key == '')
    return '-'; // default to No-Content if not set
  return key;
});

