v1(data, context) {
// console.log('var show advanced', data, context);
  if(context.cache.notFirstRun) return data.value || false;
  context.cache.notFirstRun = true;

  // Init with true if advanced stuff is set
  if(data.Type != 'C# Razor') return true;
  // check for both old/new value, as the value-init may not have run already
  if(data.Location != 'Site' && data.Location != 'Portal File System') return true;
  if(data.PublishData == true) return true;

  // don't init otherwise
  return false;
}

