  var suffix = resSuffix + (resSuffix && setSuffix ? ', ' : '') + setSuffix;
  suffix = suffix + (suffix && setIdent ? ', ' : '') + setIdent;
  if(suffix) suffix = ' (' + suffix + ')';
  return data.default + suffix;
}

