v1(data, context) {
  return data.IncludeRelationshipsAsCsv == 'true'
    ? 'CSV like "27,42,999"'
    : 'object like { id: 27, guid: "guid-guid-guidguid" }';
}

v1(data, context) {
  if(context.cache.secondRun) return data.value;
  context.cache.secondRun = true;
  return data.ContentTypeName.length > 0;
}
