Formula.*:
v1(data, context) {
  if(context.cache.secondRun) return data.value;
  context.cache.secondRun = true;

  // on new entities - set it to the new mode
  if(context.target.entity.id == 0) return 'value-label';
  return data.value;
}

Formula.en-us:
v2((data, context) => {
  const params = data.QueryParameters;
  return data.default + (params ? '  (' + params + ')' : '');
})

v2((data) => {
  // In normal picker setup, the field which must contain something is DataSources
  // Since this group is also used in old Entity-Pickers, it would need to check the EntityType
  if (!!data.DataSources && data.DataSources.length > 0)
    return true;
  if (!!data.EntityType)
    return true;
  return false;
});

v2((data, context) => {
  const options = data.optionsRaw.map(o => ({
    ...o,
    noSelect: o.entity?.Disabled ?? false,
  }))
  return {
    options: options,
  };
});

v2((data, context) => {
  const typeName = context.target.entity.type.name;
  // For custom list, it can always be visible
  if (typeName == 'UiPickerSourceCustomList'
    || typeName == 'UiPickerSourceCustomCsv'
    || typeName == 'UiPickerSourceAppAssets'
  ) {
    return true;
  }

  // For Entity Picker
  if (typeName == "UiPickerSourceEntity") {
    return data.ContentTypeNames.length > 0;
  }

  if (typeName == "UiPickerSourceQuery") {
    return data.Query.length > 0;
  }

  // Warn on unknown type, as it can't determine how to set visibility
  console.warn('formula for GroupItemHelp is running on an unexpected type, so it is not clear when to show/hide: ' + typeName);
  // console.log('2dm data', data, context);
  return data.value;
});

v2((data, context) => {
  const icons = [];
  if (!!data.ItemTooltip) icons.push('💡');
  if (!!data.ItemInformation) icons.push('');
  if (!!data.ItemHelpLink) icons.push('🔗');
  const addOn = icons.length ? ' (' + icons.join(' ') + ')' : '';
  return data.default + addOn;
});

v2((data, context) => {
  const hasAny = !!data.ItemTooltip || !!data.ItemInformation || !!data.ItemLink;
  if (!hasAny) return hasAny;
  const enabled = context.features.isEnabled('PickerUiMoreInfo');
  return !enabled;
});
