v2((data, context) => {
  const typeName = context.target.entity.type.name;
  // For custom list, it can always be visible
  if (typeName == "UiPickerSourceCustomList") {
    // console.log('custom list - show by default');
    return true;
  }

  // For Entity Picker
  if (typeName == "UiPickerSourceEntity") {
    // console.log('entity source');
    return data.ContentTypeNames.length > 0;
  }

  if (typeName == "UiPickerSourceQuery") {
    // console.log('2dm query source');
    return data.Query.length > 0;
  }

  console.warn('formula for GroupItemHelp is running on an unexpected type: ' + typeName);
  // console.log('2dm data', data, context);
  return data.value;
});

v2((data, context) => {
  const icons = [];
  if (!!data.ItemTooltip) icons.push('💡');
  if (!!data.ItemInformation) icons.push('ℹ️');
  if (!!data.ItemHelpLink) icons.push('🔗');
  const addOn = icons.length ? ' (' + icons.join(' ') + ')' : '';
  return data.default + addOn;
});

