Formula.en-us:
v1(data, context) {
  var hints = [];
  if(!data.VisibleInEditUI) hints.push('🥷🏽');
  if(data.Disabled) hints.push('🔒');
  if(data.Required) hints.push('');
  if(data.DisableTranslation) hints.push('no 🌐');
  if(data.DisableAutoTranslation === false) hints.push('auto-🌐');
  if(data.DisableAutoTranslation === true) hints.push('no auto-🌐');
  if(data.IsEphemeral) hints.push('no 💾');
  var hint = hints.length ? ' (' + hints.join(', ') + ')' : '';
  return data.default + hint; 
}

v1(data, context) {
  if(context.cache.secondRun) return data.value;
  context.cache.secondRun = true;
  return !!data.DropdownValues;
}

v1(data, context) {
  // only run in the beginning
  if (context.cache.alreadyRun) return data.value;
  context.cache.alreadyRun = true;
  return data.ButtonSource == '' && data.ButtonAdvanced == '';
}
