Formula.en-us:
v1(data, context) {
  const icon = data.Icon.toLowerCase();
  if (icon.indexOf('[app:path]/') == 0)
    return data.default + ' 👍🏽 [App:Path]/... is the best option';
  if (icon.indexOf('file:') == 0)
    return data.default + " ⚠️ using 'file:...' is not ideal, see docs link in description below";
  return data.default;
}

v1(data, context) {
  // if language, only allow crud
  return data.PermissionType == 'language' ? 'crud' : data.value;
}

v1(data, context) {
  // if language, only allow crud
  return data.PermissionType == 'language';
}

v2((data, context) => {
  const sxc = '2sxc: ' + (data.RequiredVersion || '?');
  const dnn = 'Dnn: ' + (data.RequiredDnnVersion || '?');
  const oqt = 'Oqt: ' + (data.RequiredOqtaneVersion || '❔');
  return data.default + ' (' + [sxc, dnn, oqt].join('; ') + ')';
});

v2((data, context) => {
  const token = 'Tokens: ' + (data.AllowTokenTemplates ? '✅' : '➖');
  const razor = 'Razor: ' + (data.AllowRazorTemplates ? '✅' : '➖');
  const ajax = 'Ajax: ' + (data.SupportsAjaxReload ? '✅' : '➖');
  return data.default + ' (' + [token, razor, ajax].join('; ') + ')';
});
